import React, { useState } from "react";
import axios from "axios"
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [user, setUser] = useState({
    fullName: "",
    userName: "",
    email: "",
    password: "",
  });

  const changeFullname = (e) => {
    setUser({ ...user,fullName: e.target.value });
  };
  const changeUsername = (e) => {
    setUser({...user, userName: e.target.value });
  };
  const changePassword = (e) => {
      setUser({...user, password: e.target.value });
    };
    const changeEmail = (e) => {
      setUser({...user, email: e.target.value });
    };

  const userSignUp=(e)=>{
      e.preventDefault()
      const register={
          fullName:user.fullName,
          userName:user.userName,
          email:user.email,
          password:user.password
      }
      console.log(register);
      axios.post('http://localhost:4000/signup',register) 
      .then((response)=>{
          console.log(response);
      })
  }

  return (
    <section className="vh-100 bg-image" style="background-image: url('https://mdbootstrap.com/img/Photos/new-templates/search-box/img4.jpg');">
  <div className="mask d-flex align-items-center h-100 gradient-custom-3">
    <div className="container h-100">
      <div className="row d-flex justify-content-center align-items-center h-100">
        <div className="col-12 col-md-9 col-lg-7 col-xl-6">
          <div className="card" style="border-radius: 15px;">
            <div className="card-body p-5">
              <h2 className="text-uppercase text-center mb-5">Create an account</h2>

              <form>

                <div className="form-outline mb-4">
                  <input type="text" id="form3Example1cg" className="form-control form-control-lg" />
                  <label className="form-label" for="form3Example1cg">Your Name</label>
                </div>

                <div className="form-outline mb-4">
                  <input type="email" id="form3Example3cg" className="form-control form-control-lg" />
                  <label className="form-label" for="form3Example3cg">Your Email</label>
                </div>

                <div className="form-outline mb-4">
                  <input type="password" id="form3Example4cg" className="form-control form-control-lg" />
                  <label className="form-label" for="form3Example4cg">Password</label>
                </div>

                <div className="form-outline mb-4">
                  <input type="password" id="form3Example4cdg" className="form-control form-control-lg" />
                  <label className="form-label" for="form3Example4cdg">Repeat your password</label>
                </div>

                <div className="form-check d-flex justify-content-center mb-5">
                  <input
                    className="form-check-input me-2"
                    type="checkbox"
                    value=""
                    id="form2Example3cg"
                  />
                  <label className="form-check-label" for="form2Example3g">
                    I agree all statements in <a href="#!" className="text-body"><u>Terms of service</u></a>
                  </label>
                </div>

                <div className="d-flex justify-content-center">
                  <button type="button" className="btn btn-success btn-block btn-lg gradient-custom-4 text-body">Register</button>
                </div>

                <p clasName="text-center text-muted mt-5 mb-0">Have already an account? <a href="#!" className="fw-bold text-body"><u>Login here</u></a></p>

              </form>

            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
  );
}

export default App;
