import axios from "axios";
import React, { useEffect, useState } from "react";
import './Todo.css';



function Todo(props) {

  const [todo,setTodo]=useState('')
  const [todos,setTodos]=useState([])
  const [userId,setUserId]=useState('')

  useEffect(()=>{
   let id={id:props.data}
   setUserId(props.data)
   console.log(id)
   axios.post("http://localhost:4000/gettasks",id).then((res)=>{
    console.log(resda);
     setTodos(res.data)
     console.log(todos);
   })


  },[])


  const addTodo=()=>{
    const add={
      task:todo,
      userid:userId

    }
    axios.post("http://localhost:4000/addtask",add).then((res)=>{
      if(res.data.status){
        alert(res.data.mes)
      }else{
        alert(res.data.mes)
      }
    })

  }

  return (
    <div className="app">
      <div className="mt-5 mainHeading">
        <h1>ToDo List</h1>
      </div>
      <div className="subHeading">
        <br />
        <h2>Whoop, it's Wednesday 🌝 ☕ </h2>
      </div>
      <div className="input">
        <input value={todo}  type="text" onChange={(e)=>setTodo(e.target.value)} placeholder="🖊️ Add item..." />
        <i onClick={addTodo} className="fas fa-plus"></i>
      </div>
      <div className="todos">
        <div className="todo">
          <div className="left">
            <input type="checkbox" name="" id="" />
            <p>Rect tutorial</p>
          </div>
          <div className="right">
            <i  className="fas fa-times"></i>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Todo;
