const axios= require("axios");

export const getTasks=async(_id,status)=>{

    let id = { id: _id };
    axios.post("http://localhost:4000/gettasks", id).then((res) => {
        console.log("tasksssss",res.data);
        if(status=="all"){
            let data=res.data
            return data
        }
        else if(status=="completed"){
            return null
        }

    }) 

}
