import axios from "axios";
import React, { useState } from "react";
import './Todo.css';



function Todo() {

  const [todo,setTodo]=useState('')


  const addTodo=()=>{
    alert("addding todos")
    const add={
      task:todo
    }
    axios.post("http://localhost:4000/addtask",add).then((res)=>{
      console.log("todo add ayooo",res);
      if(res.data.status){
        alert(res.data.mes)
      }else{
        alert("")
      }
    })

  }

  return (
    <div className="app">
      <div className="mt-5 mainHeading">
        <h1>ToDo List</h1>
      </div>
      <div className="subHeading">
        <br />
        <h2>Whoop, it's Wednesday ğŸŒ â˜• </h2>
      </div>
      <div className="input">
        <input value={todo}  type="text" onChange={(e)=>setTodo(e.target.value)} placeholder="ğŸ–Šï¸ Add item..." />
        <i onClick={addTodo} className="fas fa-plus"></i>
      </div>
      <div className="todos">
        <div className="todo">
          <div className="left">
            <input type="checkbox" name="" id="" />
            <p>Rect tutorial</p>
          </div>
          <div className="right">
            <i  className="fas fa-times"></i>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Todo;
